			output.Write(value.Stereo);
			output.Write(value.SampleRate);
			output.Write(value.Buffer.Length);

			for (var i = 0; i < value.Buffer.Length; i++) {
				output.Write(value.Buffer[i]);
			}
		}
	}
}

