import java.io.*;
import java.util.*;

public class ary {
    public static void main(String args[]) {
        int i, j, k, n = Integer.parseInt(args[0]);
        int x[] = new int[n];
        int y[] = new int[n];

        for (i = 0; i < n; i++)
            x[i] = i + 1;
        for (k = 0; k < 1000; k++ )
            for (j = n-1; j >= 0; j--)
                y[j] += x[j];

        System.out.println(y[0] + " " + y[n-1]);
    }
}

